// index.js
const express = require('express');
const connectDB = require('./src/modules/DB/connection.js'); // Import the connectDB function

const app = express();
const PORT = 3000;

// Connect to MongoDB
connectDB();
app.use(express.json());



// Start the server
app.listen(PORT, () => {
  console.log(`Server is running on http://localhost:${PORT}`);
});
